原CSDN博客已弃用，文章会逐渐迁移过来。 添加依赖方式见 https://blog.csdn.net/weimeig/article/details/78656734 https://blog.csdn.net/weimeig/article/details/79712595一、Get请求 创建menu action.xml &lt;?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;?&gt; &lt;menu xmlns:android=&quot;http://schemas.android.com/apk/res/android&quot;&gt; &lt;item android:title=&quot;Get&quot; android:id=&quot;@+id/menuGet&quot; /&gt; &lt;item android:id=&quot;@+id/menuResponse&quot; android:title=&quot;Response&quot; /&gt; &lt;item android:id=&quot;@+id/menuPost&quot; android:title=&quot;post&quot; /&gt; &lt;/menu&gt; activity_main.xml &lt;?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;?&gt; &lt;android.support.constraint.ConstraintLayout xmlns:android=&quot;http://schemas.android.com/apk/res/android&quot; xmlns:tools=&quot;http://schemas.android.com/tools&quot; xmlns:app=&quot;http://schemas.android.com/apk/res-auto&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot; tools:context=&quot;com.administrator.picassodemo.MainActivity&quot;&gt; &lt;ScrollView android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot;&gt; &lt;TextView android:id=&quot;@+id/tvContent&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot; /&gt; &lt;/ScrollView&gt; &lt;/android.support.constraint.ConstraintLayout&gt; MainActivity.java package com.administrator.picassodemo; import android.media.Image; import android.support.v7.app.AppCompatActivity; import android.os.Bundle; import android.util.Log; import android.view.Menu; import android.view.MenuItem; import android.widget.ImageView; import android.widget.TextView; import java.io.IOException; import java.util.concurrent.ExecutorService; import java.util.concurrent.Executors; import okhttp3.Call; import okhttp3.OkHttpClient; import okhttp3.Request; import okhttp3.Response; public class MainActivity extends AppCompatActivity { private TextView mTextView; private OkHttpClient mClient; private static final String TAG = &quot;MainActivity&quot;; @Override protected void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); setContentView(R.layout.activity_main); mTextView = (TextView)findViewById(R.id.tvContent); mClient = new OkHttpClient(); } @Override public boolean onCreateOptionsMenu(Menu menu) { getMenuInflater().inflate(R.menu.action,menu); return super.onCreateOptionsMenu(menu); } @Override public boolean onOptionsItemSelected(MenuItem item) { switch (item.getItemId()){ case R.id.menuGet: get(); break; } return super.onOptionsItemSelected(item); } private void get() { ExecutorService executor = Executors.newSingleThreadExecutor(); executor.submit(new Runnable() { @Override public void run() { // ------------------------------- Request.Builder builder = new Request.Builder(); builder.url(&quot;https://raw.githubusercontent.com/square/okhttp/master/README.md&quot;); Request request = builder.build(); Log.d(TAG, &quot;run: &quot; + request); Call call = mClient.newCall(request); try { Response response = call.execute(); if (response.isSuccessful()) { final String string = response.body().string(); // -------- runOnUiThread(new Runnable() { @Override public void run() { mTextView.setText(string); } }); // -------- } } catch (IOException e) { e.printStackTrace(); } // ------------------------------- } }); executor.shutdown(); } } 二、get-Response private void respsonse() { Request request = new Request.Builder() .url(&quot;https://raw.githubusercontent.com/square/okhttp/master/README.md&quot;) .build(); Call call = mClient.newCall(request); call.enqueue(new Callback() { @Override public void onFailure(Call call, IOException e) { Log.d(TAG,&quot;onFailure() called with:call=[&quot; +call +&quot;],e=[&quot;+ e +&quot;]&quot;); } @Override public void onResponse(Call call, Response response) throws IOException { Log.d(TAG,&quot;onResponse() called with:call=[&quot; +call +&quot;],response=[&quot;+ response +&quot;]&quot;); int code = response.code(); Headers headers = response.headers(); String content = response.body().string(); String contentType = headers.get(&quot;Content-Type&quot;); final StringBuilder buf = new StringBuilder(); buf.append(&quot;code&quot;+code); buf.append(&quot;
Headers&quot;+headers); buf.append(&quot;
Body&quot;+content); runOnUiThread(new Runnable() { @Override public void run() { mTextView.setText(buf.toString()); } }); } }); } 三、Post请求 private void respsonse() { Request request = new Request.Builder() .url(&quot;https://raw.githubusercontent.com/square/okhttp/master/README.md&quot;) .build(); Call call = mClient.newCall(request); call.enqueue(new Callback() { @Override public void onFailure(Call call, IOException e) { Log.d(TAG,&quot;onFailure() called with:call=[&quot; +call +&quot;],e=[&quot;+ e +&quot;]&quot;); } @Override public void onResponse(Call call, Response response) throws IOException { Log.d(TAG,&quot;onResponse() called with:call=[&quot; +call +&quot;],response=[&quot;+ response +&quot;]&quot;); int code = response.code(); Headers headers = response.headers(); String content = response.body().string(); String contentType = headers.get(&quot;Content-Type&quot;); final StringBuilder buf = new StringBuilder(); buf.append(&quot;code&quot;+code); buf.append(&quot;
Headers&quot;+headers); buf.append(&quot;
Body&quot;+content); runOnUiThread(new Runnable() { @Override public void run() { mTextView.setText(buf.toString()); } }); } }); } MainActivity完整代码 MainActivity.java package com.administrator.picassodemo; import android.media.Image; import android.preference.PreferenceActivity; import android.support.v7.app.AppCompatActivity; import android.os.Bundle; import android.util.Log; import android.view.Menu; import android.view.MenuItem; import android.widget.ImageView; import android.widget.TextView; import java.io.IOException; import java.time.LocalDate; import java.util.concurrent.ExecutorService; import java.util.concurrent.Executors; import okhttp3.Call; import okhttp3.Callback; import okhttp3.Headers; import okhttp3.MediaType; import okhttp3.OkHttpClient; import okhttp3.Request; import okhttp3.RequestBody; import okhttp3.Response; public class MainActivity extends AppCompatActivity { private TextView mTextView; private OkHttpClient mClient; private static final String TAG = &quot;MainActivity&quot;; private static final MediaType MEDIA_TYPE_MARKDOWN = MediaType.parse(&quot;text/x-markdown; charset=utf-8&quot;); private static final String POST_URL = &quot;https://api.github.com/markdown/raw&quot;; @Override protected void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); setContentView(R.layout.activity_main); mTextView = (TextView)findViewById(R.id.tvContent); mClient = new OkHttpClient(); } @Override public boolean onCreateOptionsMenu(Menu menu) { getMenuInflater().inflate(R.menu.action,menu); return super.onCreateOptionsMenu(menu); } @Override public boolean onOptionsItemSelected(MenuItem item) { switch (item.getItemId()){ case R.id.menuGet: get(); break; case R.id.menuResponse: respsonse(); break; case R.id.menuPost: post(); break; } return super.onOptionsItemSelected(item); } private void respsonse() { Request request = new Request.Builder() .url(&quot;https://raw.githubusercontent.com/square/okhttp/master/README.md&quot;) .build(); Call call = mClient.newCall(request); call.enqueue(new Callback() { @Override public void onFailure(Call call, IOException e) { Log.d(TAG,&quot;onFailure() called with:call=[&quot; +call +&quot;],e=[&quot;+ e +&quot;]&quot;); } @Override public void onResponse(Call call, Response response) throws IOException { Log.d(TAG,&quot;onResponse() called with:call=[&quot; +call +&quot;],response=[&quot;+ response +&quot;]&quot;); int code = response.code(); Headers headers = response.headers(); String content = response.body().string(); String contentType = headers.get(&quot;Content-Type&quot;); final StringBuilder buf = new StringBuilder(); buf.append(&quot;code&quot;+code); buf.append(&quot;
Headers&quot;+headers); buf.append(&quot;
Body&quot;+content); runOnUiThread(new Runnable() { @Override public void run() { mTextView.setText(buf.toString()); } }); } }); } private void get() { ExecutorService executor = Executors.newSingleThreadExecutor(); executor.submit(new Runnable() { @Override public void run() { // ------------------------------- Request.Builder builder = new Request.Builder(); builder.url(&quot;https://raw.githubusercontent.com/square/okhttp/master/README.md&quot;); Request request = builder.build(); Log.d(TAG, &quot;run: &quot; + request); Call call = mClient.newCall(request); try { Response response = call.execute(); if (response.isSuccessful()) { final String string = response.body().string(); // -------- runOnUiThread(new Runnable() { @Override public void run() { mTextView.setText(string); } }); // -------- } } catch (IOException e) { e.printStackTrace(); } // ------------------------------- } }); executor.shutdown(); } private void post(){ Request.Builder builder = new Request.Builder() .url(POST_URL) .post(RequestBody.create(MEDIA_TYPE_MARKDOWN,&quot;Hello world github/linguist#1 **cool**, and #1!&quot;)); Request request = builder.build(); Call call = mClient.newCall(request); call.enqueue(new Callback() { @Override public void onFailure(Call call, IOException e) { } @Override public void onResponse(Call call, Response response) throws IOException { if(response.isSuccessful()){ final String content = response.body().string(); runOnUiThread(new Runnable() { @Override public void run() { mTextView.setText(content); } }); } } }); } }

原CSDN博客已弃用，文章会逐渐迁移过来。 转帖请注明本文出自weimeig的博客（https://blog.csdn.net/weimeig/article/details/80251365），请尊重他人的辛勤劳动成果，谢谢 添加依赖 build.gradle dependencies { compile &apos;com.google.code.gson:gson:2.4&apos; } 右上角Sync Now AndroidStudio自带了将Json数据自动生成实体类的工具，详情可百度，这里不详述 初步入门案例1： 定义模拟的需要解析的数据 String cniao = &quot;{
&quot; + &quot;\&quot;sites\&quot;: [
&quot; + &quot;{ \&quot;name\&quot;:\&quot;菜鸟教程\&quot; , \&quot;url\&quot;:\&quot;m.runoob.com\&quot; }, 
&quot; + &quot;{ \&quot;name\&quot;:\&quot;google\&quot; , \&quot;url\&quot;:\&quot;www.google.com\&quot; }, 
&quot; + &quot;{ \&quot;name\&quot;:\&quot;微博\&quot; , \&quot;url\&quot;:\&quot;www.weibo.com\&quot; }
&quot; + &quot;]
&quot; + &quot;}&quot;; 定义存放被解析的数据的实体类对象 cainiao.java /** * 存放被解析的数据的实体类对象 * cainiao.java */ public static class cainiao{ private List&lt;SitesBean&gt; sites; public List&lt;SitesBean&gt; getSites() { return sites; } public void setSites(List&lt;SitesBean&gt; sites) { this.sites = sites; } public static class SitesBean { private String name; private String url; public String getName() { return name; } public void setName(String name) { this.name = name; } public String getUrl() { return url; } public void setUrl(String url) { this.url = url; } } } 数据解析 //参数1：网络请求所返还的需要解析的数据对象，参数2：要把数据解析到的实体类对象 cainiao st= new Gson().fromJson(cniao,cainiao.class); 强化练习案例2：定义模拟的需要解析的数据 String login = &quot;{
&quot; + &quot; \&quot;code\&quot;: \&quot;200\&quot;,
&quot; + &quot; \&quot;info\&quot;: \&quot;登录成功\&quot;,
&quot; + &quot; \&quot;data\&quot;: {
&quot; + &quot; \&quot;userId\&quot;: \&quot;10000\&quot;,
&quot; + &quot; \&quot;userName\&quot;: \&quot;刘德华\&quot;,
&quot; + &quot; \&quot;userDeteilInfo\&quot;: {
&quot; + &quot; \&quot;sex\&quot;: \&quot;男\&quot;
&quot; + &quot; }
&quot; + &quot; }
&quot; + &quot;}
&quot;; 定义存放被解析的数据的实体类对象 public static class Login{ private String code; private String info; private DataBean data; public String getCode() { return code; } public void setCode(String code) { this.code = code; } public String getInfo() { return info; } public void setInfo(String info) { this.info = info; } public DataBean getData() { return data; } public void setData(DataBean data) { this.data = data; } public static class DataBean { private String userId; private String userName; private UserDeteilInfoBean userDeteilInfo; public String getUserId() { return userId; } public void setUserId(String userId) { this.userId = userId; } public String getUserName() { return userName; } public void setUserName(String userName) { this.userName = userName; } public UserDeteilInfoBean getUserDeteilInfo() { return userDeteilInfo; } public void setUserDeteilInfo(UserDeteilInfoBean userDeteilInfo) { this.userDeteilInfo = userDeteilInfo; } public static class UserDeteilInfoBean { private String sex; public String getSex() { return sex; } public void setSex(String sex) { this.sex = sex; } } } } 数据解析 Login loginTest = new Gson().fromJson(login,Login.class); System.out.print(loginTest.getData().getUserName()); 对象中包含集合数据的获取方法： 当数据包含状态码，数据体等信息时，复制数据到实体类中使用AndroidStudio生成实体类为下类代码时， /** * 数据实体类 */ public static class music{ private AudioinfosBean audioinfos; public AudioinfosBean getAudioinfos() { return audioinfos; } public void setAudioinfos(AudioinfosBean audioinfos) { this.audioinfos = audioinfos; } public static class AudioinfosBean { private List&lt;CatsBean&gt; cats; public List&lt;CatsBean&gt; getCats() { return cats; } public void setCats(List&lt;CatsBean&gt; cats) { this.cats = cats; } public static class CatsBean { /** * cat_count : 3 * cat_icon_url : http://img.idaddy.cn/f/h/4/xs4ozwyc.jpg * cat_name : 热门儿歌 * cat_id : 8118 * cat_group_age_scope : -1-6 * cat_parent : 6707 */ private String cat_count; private String cat_icon_url; private String cat_name; private int cat_id; private String cat_group_age_scope; private int cat_parent; public String getCat_count() { return cat_count; } public void setCat_count(String cat_count) { this.cat_count = cat_count; } public String getCat_icon_url() { return cat_icon_url; } public void setCat_icon_url(String cat_icon_url) { this.cat_icon_url = cat_icon_url; } public String getCat_name() { return cat_name; } public void setCat_name(String cat_name) { this.cat_name = cat_name; } public int getCat_id() { return cat_id; } public void setCat_id(int cat_id) { this.cat_id = cat_id; } public String getCat_group_age_scope() { return cat_group_age_scope; } public void setCat_group_age_scope(String cat_group_age_scope) { this.cat_group_age_scope = cat_group_age_scope; } public int getCat_parent() { return cat_parent; } public void setCat_parent(int cat_parent) { this.cat_parent = cat_parent; } } } } 使用类名.方法名，直到找到包含该数据的代码块，取出数据并存到list集合中，遍历输出观察效果 music sc = new Gson().fromJson(msc,music.class); List&lt;music.AudioinfosBean.CatsBean&gt; list =sc.getAudioinfos().getCats(); for (int i = 0; i &lt; list.size(); i++) { System.out.println(list.get(i).cat_name); } 定义模拟的需要解析的数据源 String msc = &quot;{
&quot; + &quot; \&quot;audioinfos\&quot;: {
&quot; + &quot; \&quot;cats\&quot;: [
&quot; + &quot; {
&quot; + &quot; \&quot;cat_count\&quot;: \&quot;3\&quot;,
&quot; + &quot; \&quot;cat_icon_url\&quot;: \&quot;http://img.idaddy.cn/f/h/4/xs4ozwyc.jpg 
&quot; + &quot;
&quot; + &quot;\&quot;,
&quot; + &quot; \&quot;cat_name\&quot;: \&quot;热门儿歌\&quot;,
&quot; + &quot; \&quot;cat_id\&quot;: 8118,
&quot; + &quot; \&quot;cat_group_age_scope\&quot;: \&quot;-1-6\&quot;,
&quot; + &quot; \&quot;cat_parent\&quot;: 6707
&quot; + &quot; },
&quot; + &quot; {
&quot; + &quot; \&quot;cat_count\&quot;: \&quot;0\&quot;,
&quot; + &quot; \&quot;cat_icon_url\&quot;: \&quot;http://img.idaddy.cn/f/h/4/nl9ezckp.jpg 
&quot; + &quot;
&quot; + &quot;\&quot;,
&quot; + &quot; \&quot;cat_name\&quot;: \&quot;早教儿歌\&quot;,
&quot; + &quot; \&quot;cat_id\&quot;: 10719,
&quot; + &quot; \&quot;cat_group_age_scope\&quot;: \&quot;1-6\&quot;,
&quot; + &quot; \&quot;cat_parent\&quot;: 6707
&quot; + &quot; },
&quot; + &quot; {
&quot; + &quot; \&quot;cat_count\&quot;: \&quot;1\&quot;,
&quot; + &quot; \&quot;cat_icon_url\&quot;: \&quot;http://img.idaddy.cn/f/h/4/dsws0q0o.jpg 
&quot; + &quot;
&quot; + &quot;\&quot;,
&quot; + &quot; \&quot;cat_name\&quot;: \&quot;幼儿园童谣\&quot;,
&quot; + &quot; \&quot;cat_id\&quot;: 11423,
&quot; + &quot; \&quot;cat_group_age_scope\&quot;: \&quot;1-6\&quot;,
&quot; + &quot; \&quot;cat_parent\&quot;: 6707
&quot; + &quot; },
&quot; + &quot; {
&quot; + &quot; \&quot;cat_count\&quot;: \&quot;1\&quot;,
&quot; + &quot; \&quot;cat_icon_url\&quot;: \&quot;http://img.idaddy.cn/f/h/6/f5i1z0n9.jpg 
&quot; + &quot;
&quot; + &quot;\&quot;,
&quot; + &quot; \&quot;cat_name\&quot;: \&quot;韵律童谣\&quot;,
&quot; + &quot; \&quot;cat_id\&quot;: 13945,
&quot; + &quot; \&quot;cat_group_age_scope\&quot;: \&quot;2-6\&quot;,
&quot; + &quot; \&quot;cat_parent\&quot;: 6707
&quot; + &quot; },
&quot; + &quot; {
&quot; + &quot; \&quot;cat_count\&quot;: \&quot;0\&quot;,
&quot; + &quot; \&quot;cat_icon_url\&quot;: \&quot;http://img.idaddy.cn/f/h/4/k5xrsszd.jpg 
&quot; + &quot;
&quot; + &quot;\&quot;,
&quot; + &quot; \&quot;cat_name\&quot;: \&quot;幼小衔接\&quot;,
&quot; + &quot; \&quot;cat_id\&quot;: 14655,
&quot; + &quot; \&quot;cat_group_age_scope\&quot;: \&quot;2-6\&quot;,
&quot; + &quot; \&quot;cat_parent\&quot;: 6707
&quot; + &quot; }
&quot; + &quot; ]
&quot; + &quot; }
&quot; + &quot;}
&quot;; 含有集合对象的数据解析： （当Json数据中含有[]中括号时，说明该括号内的数据为集合数据，因此需要使用集合数据的数据解析方法进行解析）练习1： 服务器返回的Json数据为： [ &quot;111&quot;, &quot;222&quot;, &quot;333&quot; ] 看到中括号，说明是一段集合数据 定义模拟的需要解析的数据 String test = &quot;[
&quot; + &quot; \&quot;111\&quot;,
&quot; + &quot; \&quot;222\&quot;,
&quot; + &quot;\&quot;333\&quot;
&quot; + &quot;]&quot;; 这段数据不是键值对，直接定义一个String类型接收即可 Type type = new TypeToken&lt;List&lt;String&gt;&gt;(){}.getType();//这里的泛型将会决定你的json字符串转化的类型，一般是传实体类的对象 //参数1：满足json数组形式的字符串 //参数2：Type对象 List&lt;String&gt; list = new Gson().fromJson(test,type); System.out.println(list.get(2)); 那么问题来了，如果返回的数据是键值对类型的集合数据呢？ 比如 [ { &quot;name&quot;: &quot;宝宝乐&quot;, &quot;cast&quot;: &quot;98&quot; }, { &quot;name&quot;: &quot;宝宝乐1&quot;, &quot;cast&quot;: &quot;1289&quot; }, { &quot;name&quot;: &quot;宝宝乐2&quot;, &quot;cast&quot;: &quot;154&quot; } ] 定义模拟的需要解析的数据 String shop = &quot;[
&quot; + &quot; {
&quot; + &quot; \&quot;name\&quot;: \&quot;宝宝乐\&quot;,
&quot; + &quot; \&quot;cast\&quot;: \&quot;98\&quot;
&quot; + &quot; },
&quot; + &quot; {
&quot; + &quot; \&quot;name\&quot;: \&quot;宝宝乐1\&quot;,
&quot; + &quot; \&quot;cast\&quot;: \&quot;1289\&quot;
&quot; + &quot; },
&quot; + &quot; {
&quot; + &quot; \&quot;name\&quot;: \&quot;宝宝乐2\&quot;,
&quot; + &quot; \&quot;cast\&quot;: \&quot;154\&quot;
&quot; + &quot; }
&quot; + &quot;]&quot;; 定义存放被解析的数据的实体类对象 /** * 被解析的数据的实体类对象 */ public static class BabyShop{ private String name; private String cast; public String getName() { return name; } public void setName(String name) { this.name = name; } public String getCast() { return cast; } public void setCast(String cast) { this.cast = cast; } } 数据解析 Type type = new TypeToken&lt;List&lt;BabyShop&gt;&gt;(){}.getType();//这里的泛型将会决定你的json字符串转化的类型，一般是传实体类的对象 //参数1：满足json数组形式的字符串 //参数2：Type对象 List&lt;BabyShop&gt; list= new Gson().fromJson(shop,type); 练习2： 定义模拟的需要解析的数据 String integralss = &quot;{ \&quot;userIntegralss\&quot;:\&quot;8000\&quot;,&quot; + &quot;\&quot;List\&quot;:[ {\&quot;integralss\&quot;:\&quot;50\&quot;,&quot; + &quot;\&quot;name\&quot;:\&quot;zhangqi\&quot;},{\&quot;integralss&quot; + &quot;\&quot;:\&quot;60\&quot;,\&quot;name\&quot;:\&quot;lisi\&quot;}]}&quot;; 定义存放被解析的数据的实体类对象 Integrals.class public static class Integrals{ private String userIntegralss; private java.util.List&lt;ListBean&gt; List; public String getUserIntegralss() { return userIntegralss; } public void setUserIntegralss(String userIntegralss) { this.userIntegralss = userIntegralss; } public List&lt;ListBean&gt; getList() { return List; } public void setList(List&lt;ListBean&gt; List) { this.List = List; } public static class ListBean { private String integralss; private String name; public String getIntegralss() { return integralss; } public void setIntegralss(String integralss) { this.integralss = integralss; } public String getName() { return name; } public void setName(String name) { this.name = name; } } } 数据解析 Integrals jfw = new Gson().fromJson(integralss,Integrals.class); List&lt;Integrals.ListBean&gt; list =jfw.getList(); System.out.println(list.get(0).getIntegralss()); 将数据转换为Json数据格式 上面讲述了，服务器的Json数据如何解析到本地实体类，那么本地实体类的数据，又要怎么解析成Json数据呢？ 定义存放本地数据的实体类 public class Info{ private int code; private String info; private int data; public int getData() { return data; } public void setData(int data) { this.data = data; } public String getInfo() { return info; } public void setInfo(String info) { this.info = info; } public int getCode() { return code; } public void setCode(int code) { this.code = code; } } 定义模拟用户操作数据后，并将数据解析为Json数据的解析方法 public String login(){ Info info = new Info(); info.setCode(200); info.setData(10000); info.setInfo(&quot;注册失败&quot;); String result = new Gson().toJson(info); return result; } 调用该解析方法，并将其赋值给result对象保存 String result = login(); 那么这个result对象，便是被解析为Json数据的对象了

原CSDN博客已弃用，文章会逐渐迁移过来。 一．滚动视图的基础概念 滚动视图用于为其它组件添加滚动条,在默认的情况下,当窗体中内容比较多,而一屏显示不下时, 超出的部分不能被用户所看到.因为Android的布局管理器本身没有提供滚动屏幕的功能.如果 要让其滚动,就要使用滚动视图ScrllView. 滚动视图是FrameLayout的子类,因此,在滚动视图中,可以添加任何想要放入其中的组件,但是一个滚动视图中只能放一个组件,如果要放置多个,可以先放一个存布局管理器.再将要放置的组件放置到该布局管理器中,在滚动视图中,使用比较多的是线性布局管理器. （一）滚动视图（ScrollView）的XML配置： &lt;ScrollView android:id=&quot;@+id/myscollView&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;wrap_content&quot; &gt; //这里只能放一个布局或控件 //一般是放LinearLayout布局 &lt;/ScrollView&gt; （二）水平滚动视图(HorizontalScrollView) HorizontalScrollView和ScrollView差不多，只是滚动方式为横向XML配置： &lt;HorizontalScrollView android:id=&quot;@+id/scrollView1&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;wrap_content&quot; &gt; &lt;/HorizontalScrollView&gt; 滚动视图的作用： 1.滚动视图使用后能让里面的视图控件没展示的部分滚动后可以展示比如一个TextView默认情况文本过大后，超出屏幕或框体的内容是不能显示出来的，但是如果把这个TextView放到一个滚动视图中，就能上下滚动显示没有显示的文本内容。 2.水平滚动的视图内显示横向拉伸的布局的内容比如一个水平的LinearLayout放置十个按钮，那么只能显示五个，如果把这个LinearLayout放在一个水平滚动的视图中，就可以水平的拖动视图显示后面的按钮。 值得注意的是ListView是默认带滚动的，不需要滚动视图的包裹。 而Android的五六布局（线性布局，相对布局，绝对布局，表格布局，网格布局，层布局）和简单控件（TextView，ImageView等等）默认是不能滚动显示的，除非放到滚动视图中。 还有一点值得注意的是，如果ListView放到ScrollView中去这里ListView要自定义的拉长，否则会默认显示ListView中的一行数据，要拉伸才显示其他内容。二．简单展示的示例程序（一）布局文件activity_main.xml设计 &lt;RelativeLayout xmlns:android=&quot;http://schemas.android.com/apk/res/android&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot; &gt; &lt;ScrollView android:layout_width=&quot;wrap_content&quot; android:layout_height=&quot;wrap_content&quot; android:scrollbars=&quot;none&quot; &gt; &lt;LinearLayout android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot; android:orientation=&quot;vertical&quot; &gt; &lt;TextView android:id=&quot;@+id/tv_title&quot; android:layout_width=&quot;wrap_content&quot; android:layout_height=&quot;wrap_content&quot; /&gt; &lt;TextView android:id=&quot;@+id/tv_content&quot; android:layout_width=&quot;wrap_content&quot; android:layout_height=&quot;wrap_content&quot; /&gt; &lt;/LinearLayout&gt; &lt;/ScrollView&gt; &lt;/RelativeLayout&gt; （二）java代码设计 import android.app.Activity; import android.os.Bundle; import android.widget.TextView; /** * ScrollView的简单展示 */ public class MainActivity extends Activity { @Override protected void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); setContentView(R.layout.activity_main); TextView tvTile = (TextView) findViewById(R.id.tv_title); tvTile.setTextSize(30); tvTile.setText(&quot;文章标题：。。。&quot;); TextView tv = (TextView) findViewById(R.id.tv_content); tv.setTextSize(20); CharSequence text = &quot;锅盔的原材料都是庄稼人自家酿种的。&quot; + &quot;
&quot; + &quot;面粉是自家地里种的小麦磨成的；&quot; + &quot;
&quot; + &quot;是自家饲养的鸡产的；香豆子，&quot; + &quot;
&quot; + &quot;是自家地埂上撒出来的。这些材料，&quot; + &quot;
&quot; + &quot;也是靠老天的眷顾，才有的。山区，&quot; + &quot;
&quot; + &quot;就是靠天吃饭。万一哪一年，&quot; + &quot;
&quot; + &quot;老天一生气不下雨，山就荒了，&quot; + &quot;
&quot; + &quot;地也荒了，人也慌了，&quot; + &quot;
&quot; + &quot;妈妈的锅盔也会瘪了。&quot; + &quot;
&quot; + &quot;妈妈经过泡发面、和面、醒面等几道程序，&quot; + &quot;
&quot; + &quot;把这些材料依次揉进面里，&quot; + &quot;
&quot; + &quot;再切成碗口大的面团，揉成馒头，再用擀杖稍稍一擀，&quot; + &quot;
&quot; + &quot;或用手掌稍稍一按，就成了一寸多高、盘子大小的圆饼。&quot; + &quot;
&quot; + &quot;灵巧、细心、唯美的妈妈总不忘在饼上面&quot; + &quot;
&quot; + &quot;用菜刀画出美丽对称的图案：三角形、四边形、菱形等等。&quot; + &quot;
&quot; + &quot;妈妈的爱，就在那揉、擀、按、画的过程中，&quot; + &quot;
&quot; + &quot;一点一点渗进锅盔里，流进我的血脉里。弄好的锅&quot;; tv.setText(text); } } 程序运行后的显示界面： 页面往下拉显示的界面： 如果ListView或GridView放在ScrollVIew中要做的处理：如果是ListView在ScrollView中时的处理： import android.content.Context; import android.util.AttributeSet; import android.widget.ListView; /** * 可滚动的ListView，这里是因为这个ListView被包裹在一个ScrollView中才需要设置纵向拉伸 */ public class ScrollListView extends ListView { public ScrollListView(Context context) { super(context); } public ScrollListView(Context context, AttributeSet attrs) { super(context, attrs); } @Override protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) { int height = MeasureSpec.makeMeasureSpec(Integer.MAX_VALUE &gt;&gt; 2, MeasureSpec.AT_MOST); super.onMeasure(widthMeasureSpec, height); } } 如果是GridView在ScrollView中时的处理： import android.content.Context; import android.util.AttributeSet; import android.widget.GridView; /** * 重写网格View，设置高度 * 可滚动的GridView，这里是因为这个GridView被包裹在一个ScrollView中才需要设置纵向拉伸 */ public class ScrollGridView extends GridView { public ScrollGridView(Context context) { super(context); } public ScrollGridView(Context context, AttributeSet attrs) { super(context, attrs); } @Override protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) { int height = MeasureSpec.makeMeasureSpec(Integer.MAX_VALUE &gt;&gt; 2, MeasureSpec.AT_MOST); super.onMeasure(widthMeasureSpec, height); } } 使用的时候把这两个类分别当中ListView和GridView使用就可以了。 比如： &lt;ScrollView android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot;&gt; &lt;LinearLayout android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot; android:orientation=&quot;vertical&quot;&gt; &lt;TextView android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;wrap_content&quot; android:background=&quot;@color/hint_color&quot; android:padding=&quot;10dp&quot; android:text=&quot;历史记录&quot; /&gt; &lt;TextView android:id=&quot;@+id/tv_history&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;wrap_content&quot; android:padding=&quot;10dp&quot; android:text=&quot;当前还没有历史记录&quot; /&gt; //包名+类名 &lt;com.lwz.mathbox.weight.ScrollGridView android:id=&quot;@+id/gv_topic&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;wrap_content&quot; android:background=&quot;@color/hint_color&quot; android:horizontalSpacing=&quot;1dp&quot; android:numColumns=&quot;2&quot; android:verticalSpacing=&quot;1dp&quot; /&gt; &lt;TextView android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;wrap_content&quot; android:background=&quot;@color/hint_color&quot; android:padding=&quot;10dp&quot; android:text=&quot;社区热门&quot; /&gt; //包名+类名 &lt;com.lwz.mathbox.weight.ScrollListView android:id=&quot;@+id/lv_topic&quot; android:layout_width=&quot;match_parent&quot; android:layout_height=&quot;match_parent&quot; /&gt; &lt;/LinearLayout&gt; &lt;/ScrollView&gt;
